import React, { useEffect } from "react";
import { Card, Button, Typography, Row, Col, notification,Image, Rate } from "antd";
import { BasketRequest, Product } from "../types/Product";
import { useMutation } from "@tanstack/react-query";
import uuid from 'react-uuid';
import { addBasketService } from "../services/BasketService";
import { useNavigate } from "react-router-dom";

const { Meta } = Card;
const { Title, Text } = Typography;

type ProductCardProps = {
  product: Product;
  imgPath: string;
};

const ProductCard: React.FC<ProductCardProps> = ({ product, imgPath }) => {
  const navigate = useNavigate();
  const [review,setReview] = React.useState<number>(0);
  useEffect(() => {
    if (product.productReviews?.length != 0) {
      const lenght =product.productReviews?.length ?? 0
      var rev = 0;
      product.productReviews?.map((review) => {
        rev += review.star;
      });
      setReview(rev /lenght );
    }else {
      setReview(0);
    }
  }, [product.productReviews]);





  const addBasketMutation = useMutation({
    mutationFn: addBasketService,
    onSuccess: () => {
      notification.success({
        message: "Success",
        description: `${product.productName} added to cart`,
      });
    },
    onError: () => {
      notification.error({
        message: "Error",
        description: "An error occurred while adding to cart",
      });
    },
  });

  const addBasket = () => {
    const basket: BasketRequest = {
      basketID: uuid(),
      items: [
        {
          productID: product.productID,
          productName: product.productName,
          categoryID: product.categoryID,
          categoryName: product.category?.categoryName ?? 'Category',
          unitPrice: product.unitPrice,
          quantity: 1,
          totalPrice: product.unitPrice,
          images: product.productImages ?? [],
          discount: 0,
        },
      ],
      totalPrice: product.unitPrice,
    };
    addBasketMutation.mutate(basket);
  };

  const goToProductDetail = () => {
    navigate(`/product/${product.productID}`);
  };

  return (
    <Card
      hoverable
      style={{
        borderRadius: 10,
        overflow: "hidden",
        backgroundColor: "#f7f7f7",
        border: "1px solid #d9d9d9",
        width: "100%",
        maxWidth: "300px",
        height: "100%",
        maxHeight: "400px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
      }}

      cover={
        <Image
          alt={product.productName}
          src={product.productImages?.[0]?.imagePath ?? imgPath}
          fallback="data:image/png;base64,"
          style={{
            height: "200px",
            objectFit: "cover",
            width: "100%",
            cursor: "pointer" 
          }}
          onClick={goToProductDetail} 
        />
      }
    >
      <Meta
        description={
          <>
          <div style={{ textAlign: "center"}}>
            <Rate disabled value={review} />
          </div>
          <div style={{ textAlign: "center", padding: "10px 0" }}>
            <Title
              level={4}
              style={{ marginBottom: "5px", cursor: "pointer" }}
              onClick={goToProductDetail} 
            >
              {product.productName}
            </Title>
            <Text type="secondary" style={{ fontSize: "14px" }}>
              {product.category?.categoryName ?? 'Category'}
            </Text>
            <Row style={{ marginTop: "10px" }}>
              <Col span={12}>
                <Text strong style={{ fontSize: "16px" }}>
                  {product.unitPrice} â‚º
                </Text>
              </Col>
              <Col span={12}>
                <Button
                  type="primary"
                  style={{
                    backgroundColor: "#00b894",
                    borderColor: "#00b894",
                    borderRadius: 20,
                    width: "100%",
                  }}
                  onClick={() => addBasket()} 
                >
                  Add to Cart
                </Button>
              </Col>
            </Row>
          </div>
          </>
        }
      />
    </Card>
  );
};

export default ProductCard;
